mapper.select:
SELECT

FROM t_house_asset_exchange_request AS t1
JOIN t_house_asset_exchange_request AS t2 ON t1.asset_id = t2.target_asset AND t1.target_asset=t2.asset_id

WHERE 1=1


    AND t1.id = #{record.id}




    AND t1.user_id = #{record.userId}




    AND t1.asset_id = #{record.assetId}




    AND t1.target_asset = #{record.targetAsset}


    AND t2.user_id = #{record.targetUserId}


GROUP BY t1.id
ORDER BY t1.update_time DESC
,t1.create_time DESC

mapper.delete:
delete from t_house_asset_exchange_request
WHERE 1=1

    AND t_house_asset_exchange_request.id =#{record.id}



    AND t_house_asset_exchange_request.user_id =#{record.userId}




    AND t_house_asset_exchange_request.asset_id =#{record.assetId}



    AND t_house_asset_exchange_request.target_asset =#{record.targetAsset}



    AND t_house_asset_exchange_request.create_time=#{record.createTime}



    AND t_house_asset_exchange_request.update_time =#{record.updateTime}

mapper.select:
SELECT t_house_asset.*,
t_house_asset_exchange_request.asset_id as exchangeRequestId, t_house_property_building.cad_picture AS cadPicture, t_house_property_building.code AS buildingCode, t_house_property_building_unit.direction AS direction, t_house_property_building.issue as issue,
t_house_property_community.id AS communityId, t_house_property_community.community AS communityName, t_house_property_community.address AS address,
t_house_design_model.id AS designModelId, t_house_design_model.house_type AS houseType, t_house_design_model.house_type_picture AS houseTypePicture, t_house_vr_picture.vr_picture AS vrPicture, t_house_vr_picture.link AS vrLink, t_house_vr_picture.snapshot AS vrSnapshot, t_house_design_model.area AS area FROM t_house_asset_exchange_request JOIN t_house_asset ON t_house_asset_exchange_request.target_asset = t_house_asset.id JOIN t_house_property_building ON t_house_property_building.id = t_house_asset.building_id JOIN t_house_property_community ON t_house_property_community.id = t_house_property_building.community_id
left JOIN t_house_property_building_unit on t_house_asset.unit_id = t_house_property_building_unit.id
left JOIN t_house_design_model ON t_house_property_building_unit.design_model_id = t_house_design_model.id left join t_house_vr_picture on t_house_vr_picture.id = t_house_design_model.vr_id WHERE 1=1
AND t_house_asset_exchange_request.id =#{record.id}
AND t_house_asset_exchange_request.user_id =#{record.userId}
AND t_house_asset_exchange_request.asset_id =#{record.assetId}
AND t_house_asset_exchange_request.target_asset=#{record.targetAsset}
AND t_house_asset_exchange_request.create_time =#{record.createTime}
AND t_house_asset_exchange_request.update_time =#{record.updateTime}
AND t_house_property_community.id = #{record.communityId}

SELECT t_house_asset_exchange_request.id, t_house_asset_exchange_request.user_id AS userId, t_house_asset_exchange_request.asset_id AS assetId, t_house_asset_exchange_request.target_asset AS targetAsset, t_house_asset_exchange_request.create_time AS createTime, t_house_asset_exchange_request.update_time AS updateTime,
t_house_property_community.address AS address, t_house_property_community.id AS communityId, t_house_property_community.community As communityName,
t_house_property_building.id As buildingId, t_house_property_building.code AS buildingCode
FROM t_house_asset_exchange_request JOIN t_house_asset ON t_house_asset_exchange_request.asset_id = t_house_asset.id JOIN t_house_property_building ON t_house_property_building.id = t_house_asset.building_id JOIN t_house_property_community ON t_house_property_community.id = t_house_property_building.community_id WHERE 1=1
AND t_house_asset_exchange_request.id =#{record.id}
AND t_house_asset_exchange_request.user_id =#{record.userId}
AND t_house_asset_exchange_request.asset_id =#{record.assetId}
AND t_house_asset_exchange_request.target_asset=#{record.targetAsset}
AND t_house_asset_exchange_request.create_time =#{record.createTime}
AND t_house_asset_exchange_request.update_time =#{record.updateTime}
AND t_house_property_community.id = #{record.communityId}
group by t_house_asset_exchange_request.asset_id
