mapper.select:
SELECT t_house_user_asset.*
FROM t_house_user_asset
WHERE t_house_user_asset.asset_id = #{assetId};

SELECT t_house_user_asset.*, COUNT(*) AS assetNumber FROM t_house_user_asset JOIN t_house_asset ON t_house_user_asset.asset_id = t_house_asset.id JOIN t_house_property_building ON t_house_property_building.id = t_house_asset.building_id JOIN t_house_property_community ON t_house_property_community.id = t_house_property_building.community_id where 1=1
and t_house_user_asset.userId = #{userId}
and t_house_property_community.tenant_id=#{orgId}
GROUP BY t_house_user_asset.user_id
